import 'bootstrap/dist/css/bootstrap.min.css'
import React, { Component } from 'react';
import axios from 'axios';
import Form from './components/Form.jsx';
import LanguageList from './components/LanguageList.jsx';
import ProfileDetails from './components/ProfileDetails.jsx';
import { Alert, Grid } from 'react-bootstrap';
import { Row, Col } from 'react-bootstrap'

const mystyle = {
  color: "grey",
  padding: "60px",
  fontFamily: "sans-serif",
  fontSize: "25px",
  textAlign: "center"
};

const headerStyle = {
  color: "black",
  backgroundColor: "MediumAquaMarine",
  padding: "10px",
  fontFamily: "Arial"
};

const headerSpecific = {
  color: "white",
  padding: "10px",
  fontFamily: "sans-serif",
  fontSize: "45px"
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      gitun: 'No username',
      infoclean : '',
      formData: {
        username: '',
      }
    }
    this.handleUserFormSubmit = this.handleUserFormSubmit.bind(this);
    this.handleFormChange= this.handleFormChange.bind(this);
  }

  handleUserFormSubmit(event) {
    event.preventDefault();
       axios.get('https://api.github.com/users/'+this.state.formData.username)
    .then(response => this.setState({
      gitun: response.data.login,
      infoclean: response.data,
      info : JSON.stringify(response.data, undefined, 2)
  })).catch((err) => { console.log(err); });
 };


  handleFormChange(event) {
      const obj = this.state.formData;
      obj[event.target.name] = event.target.value;
      this.setState(obj);
  }


  render() {
    return(
      <Grid className="container">
      <div className="App">
        <header className="App-header">
          <nav class="navbar bg-warning navbar-dar">
              <h1><a style={headerSpecific} class="navbar-brand" href="#">Github API Analytics</a></h1>
                <Form
                  formData={this.state.formData}
                  handleUserFormSubmit={this.handleUserFormSubmit}
                  handleFormChange={this.handleFormChange}
                />
          </nav>
        </header>
      <div>
        <p class="container" style={mystyle} className="App-intro">
          This site provides information on GitHub user accounts.
        </p>
      </div>
      <div class="container">
            <ProfileDetails infoclean={this.state.infoclean}/>
      </div>
      <div>
        <p class="container">
            <h4>Used Languages</h4>
            <LanguageList  langlist={this.state.replanguagecount}/>
        </p>
      </div>
    </div>
    </Grid>

    );
  }
}
export default App;
